                        __global int *list_x, __global int *list_y, __global float *list_deg,
						int list_len, int width, int height) {

	//kernel index
	int x=get_global_id(0); //x
	int y=get_global_id(1); //y

	if(x<height && y<width){
		int tx,ty;
		result[y*width+x]=0;
		for(int i=0;i<list_len;++i){
			tx=x+list_x[i];
			ty=y+list_y[i];
			if( (tx>=0 && tx<height) && (ty>=0 && ty<width) ){
