         else:
             # convert to MiB
             if unit is None:
                 value *= 2**-10
                 if value < 1:
                     value = 1
             # elif unit == "M":
             #    value *= 2 ** 1
             elif unit == "G":
                 value *= 2**10
             elif unit == "T":
                 value *= 2**20
             max_memory = round(value)
